import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function Search({ id, article }) {
  const [imageUrl, setImageUrl] = useState(article[id].img);

  const handleClick = () => {
    // Update the image URL
    setImageUrl(article[id].img);
  };

  return (
    <>
      <div className="musicItem">
        <Link
          to={`/play/${article[id].title}/${article[id].img.slice(34)}/${article[id].id}`}
          style={{ "--image-url": `url(${imageUrl})` }} // Update the --image-url with the clicked image
          onClick={handleClick} // Call handleClick on link click
        >
          <img src={imageUrl} alt="" />
        </Link>
        <div className="musicTitle">
          <h3>{article[id].title}</h3>
        </div>
      </div>
    </>
  );
}
