import React, { useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import LoadingBar from "react-top-loading-bar";
import MediaPlayer from "./MediaPlayer";
import SearchEngine from "./SearchEngine";
import Spinner from "./Spinner";
export default function Body({ title }) {
  const [progress, setProgress] = useState(0);
  const [loading, setLoading] = useState(true);
  const [articles, setArticles] = useState([]);
  let arr = [];

  return (
    <div className="body">
      <BrowserRouter>
        <>
          <LoadingBar color="white" progress={progress} height={1} />
          {loading && <Spinner />}
          <SearchEngine
            arr={arr}
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"mtmusic"}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            arr={arr}
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"wunk"}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            arr={arr}
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"ressa"}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />{" "}

          <SearchEngine
            arr={arr}
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"gaama"}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
                    <SearchEngine
            arr={arr}
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"seevn"}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            arr={arr}
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"hunjama"}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <div className="mediaPlayerOut">
            <Routes>
              <Route
                path={`/play/:title/:img/:musicid`}
                exact
                element={<MediaPlayer articles={articles} arr={arr} />}
              />
            </Routes>
          </div>
        </>
      </BrowserRouter>
    </div>
  );
}
