    }
  }, [third])

  return (
    <div className="body">
      <BrowserRouter>
        <>
          <LoadingBar color="white" progress={progress} height={1} />
          {(loading && <Spinner />)}
           {/* || */}
            {/* // (articles && articles.length === 0 && <NoResults />)} */}
            <SearchEngine
              loading={loading}
              articles={articles}
              setArticles={setArticles}
              searchEngine={searchEngine}
              searchURL={searchURL}
              setLoading={setLoading}
              setProgress={setProgress}
              title={title}
              />
              {setSearchEngine("gaama")}
          <SearchEngine
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"mtmusic"}
            searchURL={searchURL}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"hunjama"}
            searchURL={searchURL}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"gaama"}
            searchURL={searchURL}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"wunk"}
            searchURL={searchURL}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"ressa"}
            searchURL={searchURL}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <SearchEngine
            loading={loading}
            articles={articles}
            setArticles={setArticles}
            searchEngine={"hemaroo"}
            searchURL={searchURL}
            setLoading={setLoading}
            setProgress={setProgress}
            title={title}
          />
          <Routes>
            <Route
              path={`/play/:id/:musicid`}
              exact
              element={<MediaPlayer articles={articles} />}
            />
          </Routes>
        </>
      </BrowserRouter>
    </div>
  );
}
