import React, { useEffect } from "react";
import { useParams } from "react-router-dom";

export default function MediaPlayer({articles}) {
  // const toggleScreenMode = () => {
  //   setScreenMode((prevMode) => (prevMode === "full" ? "half" : "full"));
  // };
  useEffect(() => {
    const { id,musicid } = useParams();

  }, [id,musicid])


  return (
    <>
      <div className="mediaplayer">
      <div className="playerImg"><img src={articles[id].img} alt="" /><h1>{articles[id].title}</h1></div>
        {/* <iframe
          src={`https://musicapi.x007.workers.dev/fetch?id=${musicid}`
          }
          width="50%"
          height="50%"
        ></iframe> */}
        <audio controls>
         <source src = {`https://musicapi.x007.workers.dev/fetch?id=${musicid}`} type = "audio/mpeg"/>
      </audio>
      </div>
    </>
  );
}
