import React, { useEffect, useRef,useState } from "react";
import { useParams } from "react-router-dom";

export default function MediaPlayer({}) {
  // const toggleScreenMode = () => {
  //   setScreenMode((prevMode) => (prevMode === "full" ? "half" : "full"));
  // };
  const { title, img, musicid } = useParams();
  const [audio, setAudio] = useState(`https://musicapi.x007.workers.dev/fetch?id=${musicid}`)
  const audio2 = document.getElementById(audio2)
  useEffect(() => {
    setAudio(`https://musicapi.x007.workers.dev/fetch?id=${musicid}`)
     audio2 = document.getElementById(audio2)
      audio2.load(); // you must call this
      setTimeout(function(){console.log('audio src:', audio2.currentSrc)});
  }, [title,img, musicid]);

  return (
    <>
      <div className="mediaplayer">
        <div className="playerImg">
          <img src={`https://hls-server.vercel.app/img/${img}`} alt="" />
          <h1>{title}</h1>
        </div>
        <audio id={audio2} controls>
          <source src={audio} type="audio/mp3" />
        </audio>
      </div>
    </>
  );
}
