import React, { useEffect,useState } from "react";
import Search from "./Search";
export default function SearchEngine(
  {loading,searchEngine,
  searchURL,
  setProgress,
  setLoading,
  title,
  articles,
  setArticles}
) {
    const [article, setArticle] = useState([])
  const updateSearch = async () => {
    const data = await fetch(searchURL);
    setLoading(true);
    setProgress(10);
    let parsedData = await data.json();
    console.log(parsedData);
    setProgress(40);
    setArticle(parsedData.response);
    setProgress(80);
    setLoading(false);
    setProgress(100);
  };
  useEffect(() => {
    // window.scrollTo({ top: 0, left: 0, behavior: "smooth" });
    updateSearch();
    setArticles(articles.concat(article))
  }, [searchEngine, title]);
  return (
    <div className="searchEngines">
      <h3 className="searchEngineName">{searchEngine}</h3>
      <div className="musicItems">
         {article && !loading &&
          article.map((element, id, result) => {
            return <Search key={id} id={parseInt(id)} articles={articles.concat(article)} />;
          }, 80)}
      </div>
    </div>
  );
}
