  // const toggleScreenMode = () => {
  //   setScreenMode((prevMode) => (prevMode === "full" ? "half" : "full"));
  // };
  const { title, img, musicid } = useParams();
  const [audio, setAudio] = useState(`https://musicapi.x007.workers.dev/fetch?id=${musicid}`)
  // let audio = useRef();
  // start the audio (using the .current property of the ref we just created) when the component mounts using the useEffect hook
  useEffect(() => {
    // audio.current = new Audio(
    //   `https://musicapi.x007.workers.dev/fetch?id=${musicid}`
    // );
    // audio.current.play();

  }, [musicid,title,img,audio]);

  // useEffect(() => {
  //   return () => {
  //     audio.current.pause();
  //     console.log("in cleanup");
  //   };
  // }, [musicid]);

  return (
    <>
      <div className="mediaplayer">
        <div className="playerImg">
          <img src={`https://hls-server.vercel.app/img/${img}`} alt="" />
          <h1>{title}</h1>
        </div>
        {/* <iframe
          src={`https://musicapi.x007.workers.dev/fetch?id=${musicid}`
          }
          width="50%"
          height="50%"
        ></iframe> */}
        <audio controls>
          <source src={`https://musicapi.x007.workers.dev/fetch?id=${musicid}`} type="audio/mpeg" />
        </audio>
      </div>
    </>
  );
}
