import React, { useEffect,useRef } from "react";
import { useParams } from "react-router-dom";

export default function MediaPlayer({ articles }) {
  // const toggleScreenMode = () => {
  //   setScreenMode((prevMode) => (prevMode === "full" ? "half" : "full"));
  // };
  const { id, musicid } = useParams();
  // useEffect(() => {
  //   console.log(id)
  //   musicid
  // }, [id,musicid])
  // the audio variable needs to be stored in a ref in order to access it across renders
  let audio = useRef();
  // start the audio (using the .current property of the ref we just created) when the component mounts using the useEffect hook
  useEffect(() => {
    audio.current = new Audio(`https://musicapi.x007.workers.dev/fetch?id=${musicid}`);
    // audio.current.play();
  }, []);
  // Stop the audio when the component unmounts
  // (not exactly what you asked re React Router, but similar idea)
  useEffect(() => {
    return () => {
      audio.current.pause();
      console.log("in cleanup");
    };
  }, [musicid]);

  return (
    <>
      <div className="mediaplayer">
        <div className="playerImg">
          <img src={articles[id].img} alt="" />
          <h1>{articles[id].title}</h1>
        </div>
        {/* <iframe
          src={`https://musicapi.x007.workers.dev/fetch?id=${musicid}`
          }
          width="50%"
          height="50%"
        ></iframe> */}
        <audio controls>
          <source
            src={audio}
            type="audio/mpeg"
          />
        </audio>
      </div>
    </>
  );
}
